<?php/** * Description of User * * @author Suharshana DsW * @web www.nysc.lk */class CourseByCenters {    public $id;    public $center_id;     public $course_id;     public $queue;    public function __construct($id) {        if ($id) {            $query = "SELECT * FROM `course_by_centers` WHERE `id`=" . $id;                         $db = new Database();            $result = mysqli_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->center_id = $result['center_id'];              $this->course_id = $result['course_id'];              $this->queue = $result['queue'];            return $result;        }    }    public function create() {                $query = "INSERT INTO `course_by_centers` (`center_id`,`course_id`,`queue`) VALUES  ('"                . $this->center_id . "','"                . $this->course_id . "','"                . $this->queue . "')";               $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return TRUE;        } else {            return FALSE;        }    }    public function all() {                $query = "SELECT * FROM `course_by_centers`";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }        public function getCoursesByCenterId($id) {                $query = "SELECT * FROM `course_by_centers` WHERE `center_id` = $id";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }        public function update() {        $query = "UPDATE  `course_by_centers` SET "                . "`course_id` ='" . $this->course_id . "', "                   . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {           $query = 'DELETE FROM `course_by_centers` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }}